#!/usr/bin/env node
import { execSync } from 'child_process'
import { fileURLToPath } from 'url'
import { dirname, resolve } from 'path'

const __filename = fileURLToPath(import.meta.url)
const __dirname = dirname(__filename)

const dockerComposePath = resolve(__dirname, '../docker-compose.yml')

export function localCofheUp() {
	execSync(`CONFIG_DIR=./configs KEYS_PATH=./keys docker compose -f "${dockerComposePath}" up -d`, { stdio: 'inherit' })
}

export function localCofheDown() {
	execSync(`CONFIG_DIR=./configs KEYS_PATH=./keys docker compose -f "${dockerComposePath}" down`, { stdio: 'inherit' })
}

export function localCofhePull() {
	execSync(`docker compose -f "${dockerComposePath}" pull`, { stdio: 'inherit' })
}
